import React, { useState } from 'react';
import "./BebidasHeladas.css";
import { emailValidation, minLengthValidation } from '../../Utils/formValidation';
import { agregarBebidaHeladaApi } from "../../Api/bebidaHelada";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBroom, faCheckCircle, faTimesCircle } from '@fortawesome/free-solid-svg-icons';
import { library } from '@fortawesome/fontawesome-svg-core';
library.add(faCheckCircle, faTimesCircle, faBroom);



export default function AgregarBebidasHeladas() {

    const [inputs, setInputs] = useState({

        codigo: "",
        nombre: "",
        ingredientes: "",
        precio: "",
        restaurante: "",
        descripcion: ""

    });

    //   const inputValidation = e => {

    //       const { type, name } = e.target;
    //
    //       if (type === "email"){

    //           setFormValid({

    //              ...formValid,
    //               [name]: emailValidation(e.target)
    //          });
    //       }


    const changeForm = e => {

        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        });
    };

    //VALIDAR FROMULARIOS
    //  const [formValid, setFormValid] = useState({

    //      Codigo: false,
    //      Nombre: false,
    //      Ingredientes: false,
    //     Precio: false,
    //     selectRestaurante: false,
    //     Descrip: false

    //  })



    const show = (e) => {

        e.preventDefault();

        const result = agregarBebidaHeladaApi(inputs);

    }

    return (

        <>
            <script src="https://kit.fontawesome.com/fdf10fb395.js" crossOrigin="anonymous"></script>

            <div className="container bordeGeneral">
                <br />
                <h1>Bebidas Frías</h1>
                <br />
                <h3 id="beCaInfo">Informacion</h3>
                <br />
                <form onSubmit={show} onChange={changeForm}>

                    <div className="row">
                        <div className="col-3">
                            <img src="https://d1nhio0ox7pgb.cloudfront.net/_img/g_collection_png/standard/512x512/lemonade_glass.png" alt="Bebidas Caliente" id="iconBeCa" />
                        </div>
                        <div className="col-sm">
                            <div className="row">
                                <div className="col-sm">
                                    <h5>Código</h5>
                                </div>
                                <div className="col-sm">
                                    <input type="text" value={inputs.codigo} name="codigo" className="form-control" placeholder="Ingrese código" />
                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="col-sm">
                                    <h5>Nombre</h5>
                                </div>
                                <div className="col-sm">
                                    <input type="text" value={inputs.nombre} name="nombre" className="form-control" placeholder="Ingrese nombre" />
                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="col-sm">
                                    <h5>Ingredientes</h5>
                                </div>
                                <div className="col-sm">
                                    <textarea value={inputs.ingredientes} name="ingredientes" cols="23" rows="3" className="form-control textArea" />
                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="col-sm">
                                    <h5>Precio</h5>
                                </div>
                                <div className="col-sm">
                                    <input type="text" value={inputs.precio} name="precio" className="form-control" placeholder="Ingrese Precio" />
                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="col-sm">
                                    <h5>Restaurante</h5>
                                </div>
                                <div className="col-sm">
                                    <select name="SelectRest" className="form-control">
                                        <option selected disabled>Seleccione Rest</option>
                                        <option value="Piccola Stella">Piccola Stella</option>
                                        <option value="Turin Anivo">Turin Anivo</option>
                                        <option value="Notte di Fuoco">Notte di Fuoco</option>
                                    </select>
                                </div>
                            </div>
                            <br />
                            <div className="row">
                                <div className="col-sm">
                                    <h5>Descripción</h5>
                                </div>
                                <div className="col-sm">
                                    <textarea value={inputs.descripcion} name="descripcion" cols="23" rows="3" className="form-control textArea"></textarea>
                                </div>
                            </div>

                        </div>
                        <div className="col-sm">
                            <div className="row">
                                <div className="col-sm">
                                    <h5>Imagen</h5>
                                </div>
                                <div className="col-sm">
                                    <img src="" alt="" id="imgBeCa" />
                                    <input type="file" id="fileBeCa" />
                                    <br /><br />
                                    <button id="findBeCa">Examinar...</button>
                                </div>
                            </div>
                            <br />
                            <br />
                            <br />
                            <br />
                            <br />
                            <br />
                            <br />
                            <br />
                            <br />
                            <div className="row" id="buttonRow">
                                <div className="col-sm">

                                    <button value="Submit" type="submit">Submit</button>
                                </div>
