import React, { useState } from 'react';
import "../FormsEstilos/AgregarPaises.css";
import { agregarPaisApi } from "../../../Api/Seguridad/paises";

export default function AgregarPaises() {


    const [inputs, setInputs] = useState({

        codigo: "",
        nombre: "",
        foto: ""
    });



    const changeForm = e => {

        // if(e.target.name = "filePais"){
        //     setInputs({
        //         ...inputs,
        //         [e.target.name]: e.target.value
        //     });

        // }

        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        });
    };

    const fileSelectedHandler = e => {

        // inputs.foto = e.target.files[0];
        const i = document.getElementById("imgPais");

        i.src = URL.createObjectURL(e.target.files[0]);
        inputs.foto = URL.createObjectURL(e.target.files[0]);
    }

    const show = (e) => {

        e.preventDefault();

        const result = agregarPaisApi(inputs);

    }

    return (

        <>
            <form onSubmit={show} onChange={changeForm}>
                <div className="container" id="empContainer">
                    <br />
                    <h1>Países</h1>

                    <div className="container">
                        <h3 id="infoEmp">Información</h3><br />
                        <div className="row">

                            <div className="col-4">
                                <img src="https://cdn0.iconfinder.com/data/icons/small-n-flat/24/678111-map-marker-512.png" alt="Empleado" id="empLogo" />
                            </div>
                            <div className="col-sm" >

                                <div className="container">
                                    <div className="row">
                                        <div className="col-sm empLabel">
                                            <h5>Código</h5>
                                        </div>
                                        <div className="col-sm">
                                            <input type="text" id="codPais" className="form-control" value={inputs.codigo} name="codigo" />
                                        </div>
                                    </div>
                                    <br />

                                    <div className="row">
                                        <div className="col-sm empLabel">
                                            <div></div>
                                            <h5>Nombre</h5>
                                        </div>
                                        <div className="col-sm">
                                            <input type="text" id="nomPais" className="form-control" value={inputs.nombre} name="nombre" />
                                        </div>

                                    </div>
                                    <br />
                                    <div className="row">
                                        <div className="col-sm">
                                            <h5>Imagen</h5>
                                        </div>
                                        <div className="col-sm">
                                            <img src="" alt="" id="imgPais" />
                                            <br />
                                            <br />
                                            <label id="addImgPais" name="addImgPais" className="btn">
                                                <input type="file" onChange={fileSelectedHandler} name="foto" />
                                            Examinar...
                                            </label>
                                        </div>
                                    </div>
                                    <br />
                                    <br />
                                    <br />
                                    <div className="row">
                                        <div className="col-sm">
                                            <button value="Submit" type="submit">Submit</button>
                                        </div>
                                        <div className="col-sm">
                                            <input type="button" value="Cancelar" name="btnCancelarPais" />
                                        </div>
                                        <div className="col-sm">
                                            <input type="button" value="Limpiar" name="btnLimpiarPais" />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </form>
        </>

    )

}
