import React from 'react';
import 'antd/dist/antd.css';
import { Route, Link, Redirect } from 'react-router-dom';
import "./StyleMenu.css";
import { logOut } from "../Api/auth"
import useAuth from "../Hooks/useAuth";

export default function LayoutRestaurante(props) {

    const { routes } = props;

    const logoutUser = () => {
        logOut()
        window.location.reload();
    }

    const { user, isLoading } = useAuth();
    console.log(user);

    if (!user && !isLoading) {
        return (
            <>
                <Route path="/" />
                <Redirect to="/" />
            </>
        )
    }

    if (user && !isLoading) {
        return (

            <div>
                <header>
                    <div className="sticky-top">
                        <nav className="navbar navbar-expand-lg navbar-light bg-light">
                            <Link to='/AdminRestaurante'><a className="navbar-brand">Home</a></Link>
                            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                                <span className="navbar-toggler-icon"></span>
                            </button>
                            <div className="collapse navbar-collapse" id="navbarSupportedContent">
                                <ul className="navbar-nav mr-auto">
                                    <li className="nav-item ">
                                        <Link to='/AdminRestaurante/ListEspeciales'><a className="nav-link">Especialidades<span className="sr-only">(current)</span></a></Link>
                                    </li>
                                    <li className="nav-item">
                                        <Link to='/AdminRestaurante/MenuBebidas'><a className="nav-link" >Menu bebidas</a></Link>
                                    </li>
                                    <li className="nav-item">
                                        <Link to='/AdminRestaurante/ListBuffet'><a className="nav-link">Buffet</a></Link>
                                    </li>
                                    <li className="nav-item">
                                        <Link to='/AdminRestaurante/Especialidades' ><a className="nav-link ">Especialidades</a></Link>
                                    </li>
                                    <li className="nav-item">
                                        <Link to='/AdminRestaurante/ListEmpleados'><a className="nav-link" >Empleados</a></Link>
                                    </li>
                                    <li id="btnAudit" className="nav-item">
                                        <Link to='/AdminRestaurante/ListPuestos'><a className="nav-link" >Puestos</a></Link>
                                    </li>
                                    <li id="btnAudit" className="nav-item">
                                        <Link to='/AdminRestaurante/ListMesas'><a className="nav-link" >Mesas</a></Link>
                                    </li>
                                    <li id="btnAudit" className="nav-item">
                                        <Link to='/AdminRestaurante/ListGaseosas'><a className="nav-link" >Gaseosas</a></Link>
                                    </li>
                                    <li id="btnAudit" className="nav-item">
                                        <Link to='/AdminRestaurante/ListBebCalientes'><a className="nav-link" >Clientes</a></Link>
                                    </li>
                                    <li id="btnAudit" className="nav-item">
                                        <Link to='/AdminRestaurante/ListBebHeladas'><a className="nav-link" >Heladas</a></Link>
                                    </li>
                                    <li id="btnAudit" className="nav-item">
                                        <Link to='/AdminRestaurante/ListLicores'><a className="nav-link" >Licores</a></Link>
                                    </li>
                                    <li id="btnAudit" className="nav-item">
                                        <Link to='/AdminRestaurante/ListVinos'><a className="nav-link" >Vinos</a></Link>
                                    </li>
                                    <li id="btnAudit" className="nav-item">
                                        <a className="nav-link" id="logoutButton" onClick={logoutUser}>Cerrar Sesión</a>
                                    </li>
                                </ul>
                            </div>
                        </nav>
                        <div className="spanheader"></div>
                    </div>
                </header>
                <div style={{ padding: '0 50px' }}>
                    <LoadRouters routes={routes} />
                </div>
            </div>

        );
    }


}


function LoadRouters({ routes }) {

    return routes.map((route, index) => (

        <Route
            key={index}
            path={route.path}
            exact={route.exact}
            component={route.component}
        />

    ));
}
